                  <motion.div
                    key={i}
                    animate={{
                      rotate: [0, 360],
                    }}
                    transition={{
                      duration: 12 + i * 2,
                      repeat: Infinity,
                      ease: "linear",
                      delay: i * 0.5,
                    }}
                    className="absolute top-1/2 left-1/2"
                    style={{
                      transformOrigin: `${radius}px 0px`,
                      transform: `translate(-50%, -50%) rotate(${angle}rad)`,
                    }}
                  >
                    <motion.div
                      animate={{
                        scale: [0.8, 1.3, 0.8],
                        opacity: [0.4, 0.8, 0.4],
                      }}
                      transition={{
                        duration: 3 + i * 0.3,
                        repeat: Infinity,
                        ease: "easeInOut",
                        delay: i * 0.2,
                      }}
                      className="w-3 h-3 rounded-full"
                      style={{
                        background: `linear-gradient(45deg, var(--neuro-accent), var(--neuro-accent-light))`,
                        boxShadow: `0 0 15px var(--neuro-accent)40`,
                      }}
                    />
                  </motion.div>
                );
              })}

              {/* Floating Tech Labels with Better Positioning */}
              <motion.div
                animate={{
                  y: [0, -15, 0],
                  rotate: [0, 3, 0],
                }}
                transition={{
                  duration: 4,
                  repeat: Infinity,
                  ease: "easeInOut",
                }}
                className="absolute -top-6 -right-6 neuro-button p-3 text-sm font-semibold gradient-text backdrop-blur-sm"
              >
                AI/ML
              </motion.div>

              <motion.div
                animate={{
                  y: [0, 12, 0],
                  rotate: [0, -3, 0],
                }}
                transition={{
                  duration: 5,
                  repeat: Infinity,
                  ease: "easeInOut",
                  delay: 1.5,
                }}
                className="absolute -bottom-6 -left-6 neuro-button p-3 text-sm font-semibold gradient-text backdrop-blur-sm"
              >
                Flutter
              </motion.div>

              <motion.div
                animate={{
                  y: [0, -8, 0],
                  rotate: [0, 2, 0],
                }}
                transition={{
                  duration: 6,
                  repeat: Infinity,
                  ease: "easeInOut",
                  delay: 3,
                }}
                className="absolute top-1/2 -left-12 neuro-button p-3 text-sm font-semibold gradient-text backdrop-blur-sm"
              >
                Python
              </motion.div>

              <motion.div
                animate={{
                  y: [0, 10, 0],
                  rotate: [0, -2, 0],
                }}
                transition={{
                  duration: 4.5,
                  repeat: Infinity,
                  ease: "easeInOut",
                  delay: 2,
                }}
                className="absolute top-1/2 -right-12 neuro-button p-3 text-sm font-semibold gradient-text backdrop-blur-sm"
              >
                Data Science
              </motion.div>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Enhanced Scroll Indicator */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 2, duration: 0.6 }}
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
      >
        <motion.button
          onClick={scrollToAbout}
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          className="neuro-button p-4 rounded-full glow-effect"
          aria-label="Scroll to about section"
        >
          <ChevronDown className="w-6 h-6 text-[var(--neuro-text-primary)]" />
        </motion.button>
        <motion.p 
          className="text-xs text-[var(--neuro-text-secondary)] mt-2 text-center"
          animate={{ opacity: [0.5, 1, 0.5] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          Scroll Down
        </p>
      </motion.div>
    </section>
  );
};

export default HeroSection;
</content>
